import { useState } from "react";
import { reqWeather } from "@/api/index";
import reqLocation from "@/api/reqLocation";
import { message } from "antd";
import useMount from "@/hooks/useMount";
import styled from "@emotion/styled";

/** 获取天气hooks */
export default function Weather() {
  const [weather, setWeather] = useState<{
    temperature?: string;
    weather?: string;
  }>({});
  const [city, setCity] = useState<string>("loading...");

  useMount(() => {
    const getWeather = async () => {
      const [err, res] = await reqLocation();
      // 没有拿到地理位置
      if (err) {
        message.error(err.message);
        return;
      }
      // 拿到了位置
      const { city, adcode } = res.result.addressComponent;
      // 设置城市
      setCity(city);
      // 请求天气
      const [e, r] = await reqWeather(adcode);
      if (e) {
        return;
      } else {
        setWeather(() => r?.lives[0]);
      }
    };
    getWeather();
  });

  const WeaterIcon = styled.img`
    margin: 0 15px;
    width: 30px;
  `;
  return (
    <>
      <WeaterIcon
        src="data:image/png;base64,"
        alt="天气图标"
      />
      {weather ? (
        <>
          <span>{weather.temperature}摄氏度</span>
          <span>「{weather.weather}」</span>
          <span>🏙{city}</span>
        </>
      ) : (
        <span>无法获取当前位置</span>
      )}
    </>
  );
}
